    .put(`/auth/user/recipes/${recipes.id}`, recipe)
    .then(res => {
        setRecipes(res.data)
    })
    .catch(err => {
        console.log(err)
    })


}
//post request to add post newPost

const addRecipe = newRecipe => {
    axiosWithAuth()
    .post('/auth/user/1', newRecipe )
    .then(res => {
      console.log(res)
        setRecipes(res.data)
    })
    .catch(err => {
        console.log(err)
    })
}

