import React, {useState, useEffect} from 'react'
import axios from 'axios'
import styled from 'styled-components'

//start stying
const Img = styled.img `
    width: 200px;
    height: 240px;
    display: block;
    margin: auto;
    border: 1px solid gray;
`

const FileSelect = styled.div`    
    width: 350px;
    margin-top: 4% 0;
    margin-left: 19%;
    padding: 4%; 
    color: #9370DB;  
`
const UploadButton = styled.button`
    height: 25px;
    width: 22%;
    justify-content: center;
    margin-left: 23.2%
    margin-top: 2%;
    border-radius: 3px;
    border: 1px solid #9370DB;
    color: #9370DB;
`
const ItemBox = styled.div `
    overflow: auto;
    border: 1px solid grey;
    width: 200px;
    height: 250px;
    padding: 4%;
    margin-left: 40%;
    border-radius: 10px;
    box-shadow: 5px 5px 10px rgba(0, 0, 0, 0.4);
    background: #fff;
`
const ButtonWrapper = styled.div `
    margin: 4%;
`

// start of uploader

const ImageUpload = () => {

    const [selectedFile, setSelectedFile] = useState([]);
// handles preview of image in div
const fileSelectedHandler = event => {
    setSelectedFile(URL.createObjectURL(event.target.files[0]));
}

// start of image upload

    const fileUploadHandler = () => {
            //form data being sent  
        const frmdata = new FormData();
            frmdata.append('ItemBox', selectedFile, selectedFile.name);
        axios
        .post('https://api.cloudinary.com/v1_1/mikezs/image/upload', frmdata, {
            onUploadProgress: progressEvent => {
            console.log('Upload Progress: ' + Math.round(progressEvent.loaded / progressEvent.total * 100) + '%')}
            })
        .then(res => {
            console.log(res);

            });


            }

    return (
        <div>
            <div>
                    <div 
                    onClick={fileUploadHandler}            
                    >Add Item</div>

                    <div>

                    <input className='choose-file'      
                    type='file'          
                    onChange={fileSelectedHandler}
                    />                                    
                    </div>
                </div>
                    <ItemBox>
                    <Img src={selectedFile} alt='item'/>
                    </ItemBox>

        </div>
      );
}

export default ImageUpload;
