import React, {useState, useEffect} from 'react';
import axios from 'axios'
import { Link } from 'react-router-dom';
import SearchRecipe from './SearchRecipe';




const UserDash = () => {
    const [recipes, setRecipes] = useState([])
    const [search, setSearch] = useState()
    const [text, setText] = useState()

    const searchword = word => {
        setText(word)
    }

    useEffect(() => {
        const abortController = new AbortController()
        const signal = AbortController.signal 
        axios
        .get('https://chefportfolio10.herokuapp.com/api/recipes', { signal: signal })
        .then(res => {
            setRecipes(res.data)
            setSearch(res.data.results)
            console.log(res.data)
        })
        .catch(err => console.log(err.res))
        return function cleanup() {
            abortController.abort()
        }
    }, [])
    function findmatch(obj) {
        if(obj.name.toLowerCase().includes(text.toLowerCase()))
        return obj
    }

    useEffect(() => {
        const results = recipes.filter(findmatch)
        setSearch(results)
    }, [text])

    if (!search)
    return (
        <div>


            {/* <SearchRecipe recipes={recipes} />            */}
           {recipes.map((item, index) => (

               <Link key={index} to={`/recipes/${item.id}`} >{item.recipe_name}{item.mealtype}

               </Link>   

           ))}

                {localStorage.getItem('token')? null :
                <nav>
                <Link to='/login'>Log In</Link>
                <Link to='/signup'>Sign Up</Link>
                </nav>
}


        </div>
        )

}
export default UserDash;
