import { usePromiseTracker } from "react-promise-tracker";
import {Logout} from './components/Logout'


function App() {
  const LoadingIndicator = () => {
    const {promiseInProgress} = usePromiseTracker()
    return (
        promiseInProgress &&
      <h1>Async call in Progress</h1>
    )
