  return (

      <div className="App">

        <UserDash/>
        <LoadingIndicator/>

        {/* <Router> */}

        {/* <PrivateRoute exact path='/chefdash' component={ChefDash}/> */}
        {/* <Route path='/login' component={Login}/> */}
        {/* <Route path='/userdash' component={UserDash}/>
        </Router>  */}
      </div>


  );
}

export default App;
