import React, { useState, useContext } from "react";
import {AuthContext} from'../Contexts/AuthContext'
import { Link } from "react-router-dom";

export const RecipeCard = props => {



    const {deleteRecipe} = useContext(AuthContext)


    return (
        <div>

            <div><span>{props.item.recipe_name}</span>
                <span>{props.item.ingredients}</span>
                <span>{props.item.instructions}</span>

            </div>


            <button>
                <Link to={`/recipes/${props.item.id}`}>Update</Link>
            </button>

            <button onClick={e => deleteRecipe(props.item.id)}>
                Delete
            </button>

            {/*Delete Button and OnClick to take us to the editRecipe */}
        </div>
    )
}
