import React, { useState, useContext, useEffect } from "react";
import {AuthContext} from'../Contexts/AuthContext'
import { Link } from "react-router-dom";
import {axiosWithAuth} from '../utils/axiosWithAuth'

export const RecipeCard = props => {

    const [recipe, setRecipe]= useState()

    console.log(props)

    useEffect(() => {
        axiosWithAuth()
        .get(`/recipes/${props.match.params.id}`)
        .then(res => setRecipe(res.data))

        .catch(err => console.log(err.res))

    }, [recipe])

    console.log(recipe)


    const {deleteRecipe} = useContext(AuthContext)


    return (
        <div>

            <div><span>{props.recipe_name}</span>
                <span>{props.ingredients}</span>
                {/* <span>{props.item.instructions}</span> */}

            </div>



                <Link to={`/recipes/${props.id}`}>Update</Link>


            <button onClick={e => deleteRecipe(props.item.id)}>
                Delete
            </button>

            {/*Delete Button and OnClick to take us to the editRecipe */}
        </div>
    )
}
