import {useState} from 'react'
import Validate from './'

const useForm = Validate => {

    const [newUser, setNewUser] = useState({
        firstName: "",
        lastName: "",
        username: "",
        password: "", 
        email: "",
        phone: '',
        state: '',
        city: "",
        streetAddress: ""          
      });

      const handleChange = e => { 
        setNewUser({
            ...newUser,
            [e.target.name]: e.target.value
        })
      };
      return {
          handleChange,
          newUser

      }
}
export default useForm;
