import React, {useState} from 'react'


const Modal = () => {
    const [showModal, setShowModal] = useState()
    showModal(false);
    const handleShowModal = e => {
        setShowModal({
            ...showModal,
            [e.target.name]: e.target.value
        })
    }
    return (
        <div>
            <h1>Modal</h1>
            <button onClick={handleShowModal} >Log In</button>
        </div>
    )
}
export default Modal;
