import React, { useState } from "react";
import {axiosWithAuth} from "../utils/axiosWithAuth";




// start of Component

const SignUp = props => {
  const [newUser, setNewUser] = useState({
    firstName: "",
    firstNameError: '',
    lastName: "",
    lastNameError: '',
    email: "",
    emailError: '',
    streetAddress: "",
    city: "",
    state: "",
    phone: '',
    username: "",
    usernameError: '',
    password: "",
    passwordError: '',


  });


  const handleChange = e => {

    setNewUser({
        ...newUser,
        [e.target.name]: e.target.value
    })
  };
  const validate = () => {
    let isError = false
    const errors = {}
    if (newUser.username.length < 3) {
      isError = true
      errors.usernameError = 'Username must be longer than 3'
    }
    if (newUser.email.includes()) {
      isError = true
      errors.usernameError = 'Username must be longer than 3'
    }
    if (isError){
      setNewUser({
        ...errors,
        ...newUser
      })
    }
    return isError;
  }
  const handleSubmit = e => {
    const err = validate();
    if(!err) {
      setNewUser({
        firstName: '',
        lastName: '',
        email: '',
        username: '',
        password: ''
      })
    }
    e.preventDefault();
    axiosWithAuth()
      .post('/auth/register', newUser)     
      .then(response => {
        console.log("registration response", response)
        localStorage.setItem('token', response.data.token)
        localStorage.setItem('userId', response.data.user.id)
        props.history.push('/chefdash')
      })
      .catch(error => console.log(error));
  };

  return (


      <div className="signup">
        <form onSubmit={handleSubmit}>
        <h1>Let's Eat</h1>
        <h3>Sign-Up</h3>
          <input
            name="firstName"
            placeholder="First Name"
            onChange={handleChange}
            value={newUser.firstName}
            type="text"
            errorText={newUser.firstNameError}
          />

          <input
            name="lastName"
            placeholder="Last Name"
            onChange={handleChange}
            value={newUser.lastName}
            type="text"
            errorText={newUser.lastNameError}
          />
          <br/>
          <input
            name="streetAddress"
            placeholder="Address"
            onChange={handleChange}
            value={newUser.streetAddress}
            type="text"
          />
          <input
            name="city"
            placeholder="City"
            onChange={handleChange}
            value={newUser.city}
            type="text"
          />
          <br/>
          <input
            name="state"
            placeholder="State"
            onChange={handleChange}
            value={newUser.state}
            type="text"
          />
          <input
            name="zipcode"
            placeholder="Zip"
            onChange={handleChange}
            value={newUser.zipCode}
            type="text"
          />
          <br/>
          <input
            name="phone"
            placeholder="Phone"
            onChange={handleChange}
            value={newUser.phone}
            type="phone"
          />

          <input
            name="email"
            placeholder="Email"
            onChange={handleChange}
            value={newUser.email}
            type="email"
            errorText={newUser.emailError}
          />
          <br/>
          <input
            name="username"
            placeholder="Username"
            onChange={handleChange}
            value={newUser.username}
            type="text"
            errorText={newUser.usernameError}
          />
          <input
            name="password"
            placeholder="Password"
            onChange={handleChange}
            value={newUser.password}
            type="password"
            errorText={newUser.passwordError}
          />
          <br/>

          <button>Submit</button>
        </form>
      </div>

  );
};

export default SignUp;
