        <div>
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    name='username'
                    value={data.username}
                    onChange={handleChange}
                    placeholder='Username'
                    />
                    <br/>
                    <input
                        type='password'
                        name='password'
                        value={data.password}
                        onChange={handleChange}
                        placeholder='Password'
                    />
                    <br/>
                    <button type='submit'>Log In</button>

            </form>
        </div>
    )

}
