import React, { useState, useContext, useEffect } from "react";
import {AuthContext} from'../Contexts/AuthContext'
import {EditContext} from'../Contexts/EditContext' 

export const EditRecipe = props => {

    const {recipeEdit} = useContext(AuthContext);
    const {recipe} = useContext(AuthContext)

    const [editRecipe, setEditRecipes] = useState({
        recipe_name: recipe.recipe_name,
        ingredients: recipe.ingredients,
        instructions: recipe.instructions
      });
console.log(props)

    

    console.log(recipe)

    const handleChange = e => {
        setEditRecipes({
          ...editRecipe,
          [e.target.name]: e.target.value
        });
      };

      const handleSubmit = e => {
        e.preventDefault();
        recipeEdit(editRecipe, props.match.params.id);
        setEditRecipes({
          recipe_name: "",
          ingredients: "",
          instructions: ""
        });
      };


    return (
<div>
            
            {/* <div><span>{props.recipe_name}</span></div>
            <img src={pictures[props.id]} alt=''/> */}

        <form onSubmit={handleSubmit}>
            <h3> Recipe Name</h3>
            <input onChange={handleChange} 
            name="recipe_name" 
            placeholder="Recipe Name"
            value ={recipe.recipe_name} />
            <br/>
            <textarea 
            onChange={handleChange} 
            name="ingredients"
            placeholder="Ingredients" 
            type="text"/>
            <br/>
            <textarea onChange={handleChange} name="instructions" 
                placeholder="Description" />
            <br/>
            <button>Update Recipe</button>
            
      </form>

</div>
    )
}

export default EditRecipe
